import { useState } from 'react';

function Square({ value, onSquareClick }) { //マスのコンポーネント

  return (
    <button
      className="square"
      onClick={onSquareClick}
    >{value}</button>
  );
}


function Board() {
  const [xIsNext, setXIsNext] = useState(true); //次がXかどうか
  const [squares, setSquares] = useState(Array(9).fill(null)); //9マスの配列

  const winner = calculateWinner(squares);
  let status;

  if (winner) {
    status = "Winner: " + winner;
  } else {
    status = "Next player: " + (xIsNext ? "X" : "O");
  }

  function handleClick(i) {
    if (squares[i] || calculateWinner(squares)) {
      return; //すでに埋まっているか、勝敗が決まっている場合は何もしない
    }

    const nextSquares = squares.slice();  //一旦配列をコピー
    //変更して
    if (xIsNext) {
      nextSquares[i] = "X";
    } else {
      nextSquares[i] = "O";
    }

    setSquares(nextSquares);              //セットする ここで描画
    setXIsNext(!xIsNext);
  }

  //handleClick(0)を呼び出す関数を作り、参照を渡す。
  return (<>
    <div className="status">{status}</div>
    <div className="board-row">
      <Square value={squares[0]} onSquareClick={() => handleClick(0)} />
      <Square value={squares[1]} onSquareClick={() => handleClick(1)} />
      <Square value={squares[2]} onSquareClick={() => handleClick(2)} />
    </div>
    <div className="board-row">
      <Square value={squares[3]} onSquareClick={() => handleClick(3)} />
      <Square value={squares[4]} onSquareClick={() => handleClick(4)} />
      <Square value={squares[5]} onSquareClick={() => handleClick(5)} />
    </div>
    <div className="board-row">
      <Square value={squares[6]} onSquareClick={() => handleClick(6)} />
      <Square value={squares[7]} onSquareClick={() => handleClick(7)} />
      <Square value={squares[8]} onSquareClick={() => handleClick(8)} />
    </div>


  </>);
}

export default function Game() {
  const [xIsNext, setXIsNext] = useState(true); //次がXかどうか
  const [history, setHistory] = useState([Array(9).fill(null)]); //9マスの配列が一個


  return (
    <div className="game">
      <div className="game-board">
        <Board />
      </div>
      <div className="game-info">
        <ol>{/*TODO*/}</ol>
      </div>
    </div>
  );
}

//勝敗判定
function calculateWinner(squares){
  const lines = [
    [0,1,2], [3,4,5], [6,7,8], //横
    [0,3,6], [1,4,7], [2,5,8], //縦
    [0,4,8], [2,4,6]           //斜め
  ];

  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
